import React, { Component } from "react";
import ReactDOM from 'react-dom';
import Tilt from 'react-parallax-tilt';


export class testimonials extends Component {
  render() {
    return (
      <div id="testimonials">
        <div className="container">
        <div className="section-title text-center">
            <h2>Industries</h2>
            <br/>
            <br/>
            <br/>
          </div>
          <div className="row">
            {this.props.data
              ? this.props.data.map((d, i) => (
                  <div key={`${d.name}-${i}`} className="col-md-4">
                    <div className="testimonial">
                        {" "}
                       <Tilt>
                           
                           <div style={{boxShadow : '10px 10px 7px 0px rgba(65, 3, 15, 0.404)' , borderRadius: '100%', width: '80%', height: '40%', background: '#750f91' }}>
              <h2 style={{paddingTop : '45%', textAlign: 'center',  font: 'message-box', fontWeight: '700', color: 'white' }}>{d.name}</h2>
                           </div>
                       </Tilt>
                        
                      </div>
                     
                      </div>
                ))
              : "loading"}
          </div>
        </div>
      </div>
    );
  }
}

export default testimonials;
