import React, { Component } from "react";
import ReactDOM from 'react-dom';
import Tilt from 'react-parallax-tilt';


export class testimonials extends Component {
  render() {
    return (
      <div id="testimonials">
        <div className="container">
        <div className="section-title text-center">
            <h2>Industries</h2>
            <br/>
            <br/>
            <br/>
          </div>
          <div className="row">
            {this.props.data
              ? this.props.data.map((d, i) => (
                  <div key={`${d.name}-${i}`} className="col-md-4">
                    <div className="testimonial">
                        {" "}
                       <Tilt>
                           <img height={'10%'} width={'20%'} src={d.img} />
                           <div style={{boxShadow : '10px 10px 7px 0px rgba(65, 3, 15, 0.404)' , borderRadius: '2%', width: '90%', height: '50%', backgroundImage: 'd.img' }}>
                           <h3 style={{padding: '3% 0% 0% 5%', textAlign: 'center' }}>{d.text}</h3>
                           </div>
                       </Tilt>
                        
                      </div>
                     
                      </div>
                ))
              : "loading"}
          </div>
        </div>
      </div>
    );
  }
}

export default testimonials;
